INSERT INTO novidades (id, title, content, imageUrl, linkUrl, authorId, createdAt) VALUES
('20251121042000', 'Revolução Sustentável: Alimentação Ecológica para Pets em 2025', 'A PriTapia lança uma nova linha de alimentos para pets produzidos a partir de resíduos orgânicos, reduzindo o desperdício alimentar e promovendo uma alimentação saudável e sustentável. Esta inovação utiliza tecnologia de ponta para transformar sobras de frutas e vegetais em rações nutritivas, alinhando-se com os princípios de sustentabilidade da ONG. Com esta iniciativa, esperamos impactar positivamente o meio ambiente e a saúde dos animais, demonstrando como a inovação pode caminhar lado a lado com a responsabilidade ecológica. Participe da campanha e ajude a construir um futuro mais verde para nossos animais de estimação.', 'https://images.unsplash.com/photo-1544568100-847a948585b9?w=800&h=400&fit=crop', 'https://pritapia.org/campanha-alimentacao-sustentavel', NULL, '2025-11-21 04:20:00');